// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import NavBar from './components/navbar';
import CreateItinerary from './pages/create-itinerary'; // Ensure the correct import path
import MyTrips from './pages/trips'; // Ensure the correct import path
import Home from './pages/home'; // Ensure the correct import path
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [trips, setTrips] = useState([]);

    const addDestination = (destination) => {
        setTrips([...trips, destination]);
    };

    const removeDestination = (index) => {
        setTrips(trips.filter((_, i) => i !== index));
    };

    return (
        <Router>
            <NavBar />
            <Routes>
                <Route path="/create-itinerary" element={<CreateItinerary addDestination={addDestination} />} />
                <Route path="/my-trips" element={<MyTrips trips={trips} removeDestination={removeDestination} />} />
                <Route path="/" element={<Home />} />
            </Routes>
        </Router>
    );
}

export default App;
