const API_URL = '/api'; // Using relative URL to leverage the proxy

export const getDestinations = async () => {
    const response = await fetch(`${API_URL}/destinations`);
    return response.json();
};

export const createDestination = async (destination) => {
    const response = await fetch(`${API_URL}/destinations`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(destination),
    });
    return response.json();
};

export const deleteDestination = async (id) => {
    const response = await fetch(`${API_URL}/destinations/${id}`, {
        method: 'DELETE',
    });
    return response.json();
};

export const updateDestination = async (id, updates) => {
    const response = await fetch(`${API_URL}/destinations/${id}`, {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(updates),
    });
    return response.json();
};
