// src/pages/MyTrips.js
import React, { useEffect, useState } from 'react';
import { getDestinations, removeDestination } from '../apiService';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

function MyTrips() {
    const [destinations, setDestinations] = useState([]);

    useEffect(() => {
        const fetchDestinations = async () => {
            try {
                const data = await getDestinations();
                setDestinations(data);
            } catch (error) {
                console.error('Failed to fetch destinations:', error);
            }
        };

        fetchDestinations();
    }, []);

    const handleRemove = async (id) => {
        try {
            await removeDestination(id);
            setDestinations(destinations.filter(destination => destination.id !== id));
        } catch (error) {
            console.error('Failed to remove destination:', error);
        }
    };

    return (
        <div>
            <h3 className="container text-center">My Trips</h3>
            <div className="d-flex flex-wrap">
                {destinations.length === 0 ? (
                    <p>No trips added yet.</p>
                ) : (
                    destinations.map((destination, index) => (
                        <Card key={index} style={{ width: '18rem', margin: '10px' }}>
                            <Card.Body>
                                <Card.Title>{destination.name}</Card.Title>
                                <Card.Text>
                                    {destination.description}
                                </Card.Text>
                                <Button variant="danger" onClick={() => handleRemove(destination.id)}>
                                    Remove from My Trips
                                </Button>
                            </Card.Body>
                        </Card>
                    ))
                )}
            </div>
        </div>
    );
}

export default MyTrips;
