// src/contexts/TripsContext.js
import React, { createContext, useState } from 'react';

export const TripsContext = createContext();

export const TripsProvider = ({ children }) => {
    const [trips, setTrips] = useState([
        {
            id: 1,
            name: 'France',
            cities: ['Paris'],
            description: 'France is known for its cuisine, art, and culture.'
        },
        {
            id: 2,
            name: 'Italy',
            cities: ['Rome'],
            description: 'Italy is known for its rich history, art, and cuisine.'
        }
        // Add other initial trips if needed
    ]);

    const addTrip = (trip) => {
        setTrips([...trips, { ...trip, id: trips.length + 1 }]);
    };

    const removeTrip = (id) => {
        setTrips(trips.filter(trip => trip.id !== id));
    };

    return (
        <TripsContext.Provider value={{ trips, addTrip, removeTrip }}>
            {children}
        </TripsContext.Provider>
    );
};
