const API_URL = '/api'; 

const handleResponse = async (response) => {
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.message || 'Unknown error');
  }
  return response.json();
};

export const getDestinations = async () => {
  const response = await fetch(`${API_URL}/destinations/list`);
  return handleResponse(response);
};

export const createDestination = async (destination) => {
  const response = await fetch(`${API_URL}/destinations/create`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(destination),
  });
  return handleResponse(response);
};

export const updateDestination = async (id, updates) => {
  const response = await fetch(`${API_URL}/destinations/update`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ id, ...updates }),
  });
  return handleResponse(response);
};

export const deleteDestination = async (id) => {
  const response = await fetch(`${API_URL}/destinations/delete`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ id }),
  });
  return handleResponse(response);
};
