import React, { useState, useEffect } from 'react';
import { getDestinations, createDestination } from '../apiService';
import CountryView from '../components/cardView';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

function CreateItinerary() {
    const [destinations, setDestinations] = useState([]);
    const [trips, setTrips] = useState([]);

    useEffect(() => {
        const fetchDestinations = async () => {
            try {
                const data = await getDestinations();
                setDestinations(data);
            } catch (error) {
                console.error('Failed to fetch destinations:', error);
            }
        };

        fetchDestinations();
    }, []);

    const addDestination = async (destination) => {
        try {
            const newDestination = await createDestination(destination);
            setTrips([...trips, newDestination]);
        } catch (error) {
            console.error('Failed to create destination:', error);
        }
    };

    const removeDestination = (index) => {
        setTrips(trips.filter((_, i) => i !== index));
    };

    return (
        <div className="container">
            <h3 className="text-center">Create your own trip</h3>
            <p style={paragraphStyle}>
                "In the heart of every traveler lies a deep-rooted desire to explore, 
                to wander through the realms of the unknown, and to discover the treasures that await beyond the horizon.
                It's an insatiable craving, an irresistible urge that beckons us to set foot on unfamiliar lands,
                to immerse ourselves in diverse cultures, and to embrace the exhilarating beauty of the world."
            </p>
            <CountryView addDestination={addDestination} destinations={destinations} />

            <div className="mt-5">
                <h4>Added Trips</h4>
                {trips.length === 0 ? (
                    <p>No trips added yet.</p>
                ) : (
                    <div className="d-flex flex-wrap justify-content-center">
                        {trips.map((trip, index) => (
                            <Card key={index} style={{ width: '18rem', margin: '10px' }}>
                                <Card.Body>
                                    <Card.Title>{trip.name}</Card.Title>
                                    <Card.Text>
                                        {trip.description}
                                    </Card.Text>
                                    <Button variant="danger" onClick={() => removeDestination(index)}>
                                        Remove
                                    </Button>
                                </Card.Body>
                            </Card>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

const paragraphStyle = {
    fontSize: '1.2rem',
    lineHeight: '1.6',
    color: '#333',
    marginBottom: '20px',
    textAlign: 'center',
};

export default CreateItinerary;
