// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import NavBar from './components/navbar';
import CreateItinerary from './pages/create-itinerary';
import MyTrips from './pages/trips';
import Home from './pages/home';
import MyAccount from './pages/my-account'; // Ensure correct import
import AuthPage from './pages/auth-page'; // Ensure correct import
import { AuthProvider } from './contexts/AuthContext';
import './i18n'
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [trips, setTrips] = useState([]);

    const addDestination = (destination) => {
        setTrips([...trips, destination]);
    };

    const removeDestination = (index) => {
        setTrips(trips.filter((_, i) => i !== index));
    };

    return (
        <AuthProvider>
            <Router>
                <NavBar />
                <Routes>
                    <Route path="/create-itinerary" element={<CreateItinerary addDestination={addDestination} />} />
                    <Route path="/my-trips" element={<MyTrips trips={trips} removeDestination={removeDestination} />} />
                    <Route path="/my-account" element={<MyAccount />} /> {/* Ensure this route is correct */}
                    <Route path="/auth" element={<AuthPage />} />
                    <Route path="/" element={<Home />} />
                </Routes>
            </Router>
        </AuthProvider>
    );
}

export default App;
