// src/contexts/TripsContext.js
import React, { createContext, useState } from 'react';

export const TripsContext = createContext();

export const TripsProvider = ({ children }) => {
    const [trips, setTrips] = useState([
        { id: 1, name: 'France', cities: ['Paris'], itinerary: ['Eiffel Tower', 'Louvre Museum'] },
        { id: 2, name: 'Italy', cities: ['Rome'], itinerary: ['Colosseum', 'Vatican Museums'] }
    ]);

    const removeTrip = (id) => {
        setTrips(trips.filter(trip => trip.id !== id));
    };

    return (
        <TripsContext.Provider value={{ trips, removeTrip }}>
            {children}
        </TripsContext.Provider>
    );
};
