// src/components/cardView.js
import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';

const countries = [
    {
        name: 'France',
        cities: ['Paris'],
        description: 'France is known for its cuisine, art, and culture.',
        itinerary: [
          {
            day: 1,
            activities: [
              {
                time: 'Morning',
                description: 'Arrive in Paris and check into your hotel. Have breakfast at a local café, enjoying pastries and coffee.'
              },
              {
                time: 'Afternoon',
                description: 'Visit the Eiffel Tower. Take the elevator to the top for a panoramic view of Paris. Walk along the Champ de Mars and enjoy a picnic.'
              },
              {
                time: 'Evening',
                description: 'Dinner at a Seine River cruise restaurant to enjoy the city lights and landmarks.'
              }
            ]
          },
          {
            day: 2,
            activities: [
              {
                time: 'Morning',
                description: 'Visit the Louvre Museum. Spend a few hours exploring its vast collection, including the Mona Lisa and the Venus de Milo.'
              },
              {
                time: 'Afternoon',
                description: 'Walk to the Jardin des Tuileries for a relaxing stroll. Visit the Musée d\'Orsay to see impressionist masterpieces.'
              },
              {
                time: 'Evening',
                description: 'Dinner in the Latin Quarter. Try some traditional French cuisine.'
              }
            ]
          },
          {
            day: 3,
            activities: [
              {
                time: 'Morning',
                description: 'Visit Notre-Dame Cathedral. Take a tour of the interior and climb the towers for a great view. Walk to the Sainte-Chapelle to see its stunning stained-glass windows.'
              },
              {
                time: 'Afternoon',
                description: 'Head to Montmartre. Visit the Sacré-Cœur Basilica and explore the charming streets of this artistic neighborhood.'
              },
              {
                time: 'Evening',
                description: 'Enjoy dinner at a bistro in Montmartre and watch a cabaret show at the Moulin Rouge.'
              }
            ]
          },
          {
            day: 4,
            activities: [
              {
                time: 'Morning',
                description: 'Take a day trip to the Palace of Versailles. Tour the opulent palace, the Hall of Mirrors, and the expansive gardens.'
              },
              {
                time: 'Afternoon',
                description: 'Continue exploring Versailles, including the Trianon Palaces and Marie Antoinette\'s Estate.'
              },
              {
                time: 'Evening',
                description: 'Return to Paris and have dinner at a Michelin-starred restaurant.'
              }
            ]
          },
          {
            day: 5,
            activities: [
              {
                time: 'Morning',
                description: 'Visit the Champs-Élysées for some shopping. Explore high-end boutiques and iconic stores. Stop by the Arc de Triomphe and climb to the top for another great view of the city.'
              },
              {
                time: 'Afternoon',
                description: 'Explore Le Marais district. Visit trendy shops, art galleries, and enjoy a leisurely lunch at a café.'
              },
              {
                time: 'Evening',
                description: 'Have a farewell dinner at a restaurant with a view of the Eiffel Tower. Take an evening walk along the Seine, enjoying the romantic atmosphere.'
              }
            ]
          },
          {
            day: 6,
            activities: [
              {
                time: 'Morning',
                description: 'Enjoy a leisurely breakfast at your hotel or a nearby café. Depending on your flight time, visit any last-minute attractions or shops. Check out of your hotel and head to the airport for your departure.'
              }
            ]
          }
        ]
      }

];

function CountryView({ addDestination }) {
    const [show, setShow] = useState(false);
    const [currentDescription, setCurrentDescription] = useState('');

    const handleClose = () => setShow(false);
    const handleShow = (description) => {
        setCurrentDescription(description);
        setShow(true);
    };

    return (
        <div className="d-flex flex-wrap">
            {countries.map((country, index) => (
                <Card key={index} style={{ width: '18rem', margin: '10px' }}>
                    <Card.Body>
                        <Card.Title>{country.name}</Card.Title>
                        <Card.Text>
                            {country.cities.join(', ')}
                        </Card.Text>
                        <Button variant="primary" onClick={() => addDestination(country)}>
                            Add to My Trips
                        </Button>
                        <Button variant="secondary" style={{ marginTop: '10px' }} onClick={() => handleShow(country.description)}>
                            View Description
                        </Button>
                    </Card.Body>
                </Card>
            ))}

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Country Description</Modal.Title>
                </Modal.Header>
                <Modal.Body>{currentDescription}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default CountryView;
