// src/components/ItineraryModal.js
import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const ItineraryModal = ({ show, handleClose, trip }) => {
  if (!trip) return null;

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Itinerary for {trip.country} - {trip.city}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* Assuming trip.itinerary is an array of strings */}
        {trip.itinerary && trip.itinerary.length > 0 ? (
          <ul>
            {trip.itinerary.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        ) : (
          <p>No itinerary available for this trip.</p>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ItineraryModal;
