// src/pages/my-trips.js
import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import './my-trips.css'; // Add your custom CSS file

function MyTrips({ trips, removeDestination }) {
    const [show, setShow] = useState(false);
    const [currentDescription, setCurrentDescription] = useState('');

    const handleClose = () => setShow(false);
    const handleShow = (description) => {
        setCurrentDescription(description);
        setShow(true);
    };

    return (
        <div className="container">
            <h3>My Trips</h3>
            {trips.length === 0 ? (
                <p>No trips added yet.</p>
            ) : (
                <div className="d-flex flex-wrap">
                    {trips.map((trip, index) => (
                        <Card key={index} style={{ width: '18rem', margin: '10px' }}>
                            <Card.Body>
                                <Card.Title>{trip.name}</Card.Title>
                                <Card.Text>
                                    {trip.cities.join(', ')}
                                </Card.Text>
                                <div className="button-container">
                                    <Button className="btn - btn-danger" onClick={() => removeDestination(index)}>
                                        Remove
                                    </Button>
                                    <Button className="btn-flat btn-description" onClick={() => handleShow(trip.description)}>
                                        View Description
                                    </Button>
                                </div>
                            </Card.Body>
                        </Card>
                    ))}
                </div>
            )}

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Country Description</Modal.Title>
                </Modal.Header>
                <Modal.Body>{currentDescription}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default MyTrips;
