// src/components/navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import { Backpack, UserCircleGear } from 'phosphor-react';
import { useTranslation } from 'react-i18next';
import { useAuth } from '../contexts/AuthContext';

function NavBar() {
    const { t, i18n } = useTranslation();
    const { user, logout } = useAuth();

    const changeLanguage = (lng) => {
        i18n.changeLanguage(lng);
    };

    return (
        <Navbar expand="lg" className="bg-body-tertiary">
            <Container>
                <Navbar.Brand>
                    <Link to="/" style={{ textDecoration: 'none', color: 'black' }}>
                        Voyage-Voyage
                    </Link>
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link>
                            <Link to="/my-account" style={{ textDecoration: 'none', marginRight: '50px', display: 'flex', alignItems: 'center', color: 'black' }}>
                                <UserCircleGear size={32} />
                                {t('My Account')}
                            </Link>
                        </Nav.Link>
                        <Nav.Link>
                            <Link to="/my-trips" style={{ textDecoration: 'none', marginRight: '50px', display: 'flex', alignItems: 'center', color: 'black' }}>
                                <Backpack size={32} />
                                {t('My Trips')}
                            </Link>
                        </Nav.Link>
                    </Nav>
                    {user && (
                        <>
                            <Navbar.Text style={{ marginLeft: 'auto' }}>
                                {t('signed')}: <span>{user.username}</span>
                            </Navbar.Text>
                            <Button variant="link" onClick={logout} style={{ marginLeft: '10px' }}>
                                {t('log out')}
                            </Button>
                        </>
                    )}
                    <div className="language-switcher" style={{ marginLeft: '10px' }}>
                        <button onClick={() => changeLanguage('en')}>EN</button>
                        <button onClick={() => changeLanguage('cs')}>CZ</button>
                    </div>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
}

export default NavBar;
